import React, { useEffect, useState } from "react";
import axios from "axios";
import { connect } from "react-redux";
import {
  MDBNav,
  MDBNavItem,
  MDBNavLink,
  MDBView,
  MDBMask,
  MDBContainer,
  MDBCarousel,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBAnimation,
} from "mdbreact";
import { Fade } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";
import "./Landing.css";
import "../MainImage/MainImage.css";
import { Carousel } from "react-responsive-carousel";
import { fetchItems } from "../../actions/movie";
import { API_KEY, API_URL } from "../../config"


let endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=1`;

const Landing = ({ movies }) => {


  let [index, setIndex] = useState(0);

  useEffect(() => {
    timer();
  }, [index]);


  useEffect(() => {
    fetchItems(endpoint)
  }, [index >= 20])

  const timer = () => {
    setInterval(() => {
      setIndex(index++);
    }, 3000);
  };

  return (
    <div>
      <MDBCarousel
        activeItem={1}
        length={20}
        showControls={false}
        showIndicators={false}
        className="z-depth-1"
        slide
      >
        <MDBCarouselInner>
          <MDBCarouselItem>
            <MDBAnimation type="fadeIn">
              <MDBView>
                <img
                  className="d-block w-100"
                  src={`http://image.tmdb.org/t/p/w1280${movies[index].backdrop_path}`}
                  alt="first slide"
                />
              </MDBView>
            </MDBAnimation>
          </MDBCarouselItem>
        </MDBCarouselInner>
      </MDBCarousel>
    </div>
  );
};

const mapStateToProps = (state) => ({
  movies: state.movie.movies,
});

export default connect(mapStateToProps, {})(Landing);
