import React, { useEffect, useState } from "react";
import config from "../../config.json";
import PropTypes from "prop-types";
import SpinnerPage from "../Layout/SpinnerPage";
import { addToCart, loadCart } from "../../actions/cart";
import { setMovies, fetchApi, nextPage, prevPage } from "../../actions/movie";
import { connect } from "react-redux";
import Movie from "./Movie";
import SearchPage from "../Search/Search";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBView,
  MDBIcon,
  MDBAnimation,
  MDBBtn,
} from "mdbreact";
import RelatedMovies from "./RelatedMovies";
import "../../App.css";
import { useAuth0 } from "@auth0/auth0-react";
import { useHistory } from "react-router-dom";

const MovieList = ({
  addToCart,
  loadCart,
  isLoading,
  movies,
  fetchApi,
  // page,
  nextPage,
  prevPage,
  userId,
}) => {
  const { getAccessTokenSilently, isAuthenticated, user } = useAuth0();

  const history = useHistory();

  let [page, setPage] = useState(1);
  let [flix, setMovies] = useState(movies);
  let [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchApi(config.API_KEY, page);
    loadCart();
    history.push(`${page}`);
  }, [page]);

  let load = (
    <div>
      <SpinnerPage />
    </div>
  );

  const loadMore = () => {
    setPage(page + 1);
    let newMovies = fetchApi(config.API_KEY, page);
    newMovies.then((m) => setMovies(flix.concat(m)));
  };

  // page transition
  let pages = (
    <nav aria-label="Page navigation example">
      <ul className="pagination">
        <MDBBtn
          onClick={() => setPage(page === 1 ? (page = 1) : page - 1)}
          className="page-item"
        >
          <MDBIcon
            className="white-text pr-3"
            size="2x"
            icon="angle-double-left"
          />
        </MDBBtn>
        &nbsp; &nbsp; &nbsp; Page: {page} &nbsp; &nbsp; &nbsp; &nbsp;
        <MDBBtn onClick={() => setPage(page + 1)} className="page-item">
          <MDBIcon
            className="white-text pr-3"
            size="2x"
            icon="angle-double-right"
          />
        </MDBBtn>
      </ul>
    </nav>
  );

  const movieList = (
    // <MDBAnimation type="zoomIn" duration="1s">
      <MDBRow>
        {flix.map((movie, index) => {
          return (
            <MDBCol middle="true" size="3">
              <Movie
                index={index}
                id={movie.id}
                addToCart={addToCart}
                title={movie.title}
                image={movie.poster_path}
                overview={movie.overview}
                releaseDate={movie.release_date}
                price={2.99}
                movieObj={movie}
              />
            </MDBCol>
          );
        })}
      </MDBRow>
    // </MDBAnimation>
  );

  return (
    <MDBContainer>
      <SearchPage />
      <MDBContainer>
        {isLoading ? load : movieList}{" "}
        {
          (loading ? load : setLoading(true),
          (
            <MDBBtn onClick={() => loadMore()} className="pagination">
              Load More
            </MDBBtn>
          ))
        }
      </MDBContainer>

      <RelatedMovies />
    </MDBContainer>
  );
};

MovieList.propTypes = {
  addToCart: PropTypes.func.isRequired,
  loadCart: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  userId: state.auth.userInfo._id,
  isLoading: state.movie.isLoading,
  authenticated: state.auth.authenticated,
  movies: state.movie.movies,
  // page: state.movie.moviePage,
  searchedMovie: state.movie.searchedMovie,
});

export default connect(mapStateToProps, {
  addToCart,
  loadCart,
  setMovies,
  fetchApi,
  nextPage,
  prevPage,
})(MovieList);
