import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { getShow } from "../../actions/movie";
// import SpinnerPage from "../Layout/SpinnerPage";
import { addToCartTvShow } from "../../actions/cart";
import {
  MDBView,
  MDBContainer,
  MDBBtn,
  MDBIcon,
  MDBAnimation,
  MDBRow,
  MDBCol,
} from "mdbreact";

const Show = ({
  id,
  image,
  getShow,
  isLoading,
  tvShowObj,
  price,
  addToCartTvShow,
}) => {
  let showList = (
    <MDBContainer>
      <MDBRow>
        <MDBCol>
          <div style={{ textAlign: "", margin: "20px" }}>
            <MDBView hover zoom>
              <Link to={"/show_details/" + id} onClick={() => getShow(id)}>
                <img
                  className="movie-container"
                  src={`http://image.tmdb.org/t/p/w185${image}`}
                />
              </Link>
            </MDBView>

            <h5>{tvShowObj.name}</h5>
            <h6>({tvShowObj.first_air_date.slice(0, 4)})</h6>
          </div>

          <h5>${price}</h5>
          <MDBBtn onClick={() => addToCartTvShow(tvShowObj)}>
            Add To Cart <MDBIcon icon="cart-plus" />
          </MDBBtn>
          <div></div>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );

  return (
    <div>
      <MDBAnimation type="zoomIn" duration="1s">
        {showList}
      </MDBAnimation>
    </div>
  );
};

const mapStateToProps = (state) => ({
  isLoading: state.movie.isLoading,
});

export default connect(mapStateToProps, { getShow, addToCartTvShow })(Show);
