import React, { useEffect, useState } from "react";
import axios from "axios";
import { connect } from "react-redux";
import {
  MDBNav,
  MDBNavItem,
  MDBNavLink,
  MDBView,
  MDBMask,
  MDBContainer,
  MDBCarousel,
  MDBCarouselInner,
  MDBCarouselItem,
} from "mdbreact";
import { useSpring, useTransition, animated, config } from "react-spring";

const Landing = ({ movies }) => {



  useEffect(() => {
    console.log(movies)
  }, []);

  const [index, set] = useState(0);

  const transitions = useTransition(movies[index], (item) => item.id, {
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    config: config.molasses,
  });

  useEffect(
    () => void setInterval(() => set((state) => (state + 1) % 4), 2000),
    []
  );

  let slidePics = transitions.map(({ item, props, key }) => (
    <animated.div
      key={key}
      class="bg"
      style={{
        ...props,
        backgroundImage: `url(http://image.tmdb.org/t/p/w1280${item.poster_path})`,
      }}
    />
  ));



  return (
    <div className="rmdb-moviethumb">
      {/* {movies.map((m, i) => (
        <MDBCarousel
          activeItem={1}
          length={100}
          showControls={false}
          showIndicators={false}
          className="z-depth-1"
          slide
        >
          <MDBCarouselInner>
            <MDBCarouselItem itemId={`${m[i]}`}>
              <MDBView>
                <img
                  className="d-block w-100"
                  src={`http://image.tmdb.org/t/p/w1280${m.poster_path}`}
                  alt="First slide"
                />
              </MDBView>
            </MDBCarouselItem>
          </MDBCarouselInner>
        </MDBCarousel>
      ))} */}
      {slidePics}

      {/* <main>
        <MDBContainer className="text-center my-5"></MDBContainer>
      </main> */}
    </div>
  );
};

const mapStateToProps = (state) => ({
  movies: state.movie.movies,
});

export default connect(mapStateToProps, {})(Landing);
