import React, { useEffect } from "react";
import { connect } from "react-redux";
import Img from "react-image";
import PropTypes from "prop-types";
import {
  MDBContainer,
  MDBBtn,
  MDBNavLink,
  MDBTable,
  MDBTableHead,
  MDBTableBody,
} from "mdbreact";
import { deleteItem, loadCart, getPriceTotal } from "../../actions/cart";
import Spinner from "../Spinner/Spinner";

const Cart = ({
  cart,
  loadCart,
  getPriceTotal,
  total,
  loading,
  deleteItem,
  price = 2.99,
  userId,
}) => {
  useEffect(() => {
    loadCart();
    getPriceTotal(userId);
  }, [loading, total]);

  if (loading) {
    return <Spinner />;
  }

  let cartItems = (
    <div>
      <MDBTable>
        <MDBTableHead>
          <tr>
            <th>Movie</th>
            <th>Price</th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {cart.map((movie, index) => (
            <tr key={index}>
              <div>
                <td>
                  <Img
                    src={`https://image.tmdb.org/t/p/w92${movie.poster_path}`}
                  />
                  <h5>
                    {!movie.original_title ? movie.name : movie.original_title}
                  </h5>
                </td>
              </div>
              <td>
                <div>${price}</div>
              </td>
              <td>
                <MDBBtn onClick={() => deleteItem(movie.id, index, price)}>
                  Remove
                </MDBBtn>
              </td>
            </tr>
          ))}
        </MDBTableBody>
      </MDBTable>
    </div>
  );

  let checkOut = (
    <div style={{ marginLeft: "10px", color: "white" }}>
      <h2>Total: ${total.toFixed(2)}</h2>
      <MDBNavLink to="/checkout">
        <MDBBtn>Check Out</MDBBtn>
      </MDBNavLink>
    </div>
  );

  return (
    <MDBContainer>
      <div style={{ marginTop: "100px" }}>
        <h3>Cart: {cart.length} item(s)</h3>
        <div>{cartItems}</div>
        <div>{checkOut}</div>
      </div>
    </MDBContainer>
  );
};

Cart.propTypes = {
  loadCart: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  cart: state.cart.cart,
  total: state.cart.totalPrice,
  loading: state.cart.loading,
  userId: state.auth.userInfo._id,
});

export default connect(mapStateToProps, {
  deleteItem,
  loadCart,
  getPriceTotal,
})(Cart);
