import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { getMovie, getMovieIds } from "../../actions/movie";
import SpinnerPage from "../Layout/SpinnerPage";
import { addToCart, loadCart } from "../../actions/cart";
import { Btn, Icon, Spinner } from "@material-ui/core";
import moment from "moment";
import MovieDetails from "./MovieDetails";


const Movie = ({
  id,
  image,
  getMovie,
  isLoading,
  addToCart,
  movie,
  movieObj,
  price,
  releaseDate,
  title,
  userId,
  index
}) => {


  let movieLink = (
    <div style={{ color: "white"}}>
      <View hover zoom>
        <Link to={"/movieInfo/" + id} onClick={() => getMovie(id)}>
          <img src={`http://image.tmdb.org/t/p/w185${image}`} />
        </Link>
      </View>
      <div>
        <h5>{title}</h5>
        <h6>{moment(releaseDate).format("LL")}</h6>
        <h5>${price}</h5>
      </div>

      <Btn onClick={() => addToCart(movieObj, index)}>
        Add To Cart <Icon icon="cart-plus" />
      </Btn>
    </div>
  );

  return <div>{movieLink}</div>;
};

const mapStateToProps = (state) => ({
  isLoading: state.movie.isLoading,
  userId: state.auth.userInfo._id
});

export default connect(mapStateToProps, { getMovie, addToCart })(Movie);
