import React, { Component, useEffect, useState } from "react";
import config from "../../config.json";
import PropTypes from "prop-types";
import SpinnerPage from "../Layout/SpinnerPage";
import { addToCart, loadCart, getCart } from "../../actions/cart";
import {
  getMovie,
  setMovies,
  clearCache,
  getMovieVideo,
  fetchApi,
  nextPage,
  prevPage,
  setRelatedMovies,
  setMoviesDB,
} from "../../actions/movie";
import { connect } from "react-redux";
import Movie from "./Movie";
import SearchPage from "../Search/Search";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBView,
  MDBIcon,
  MDBAnimation,
  MDBNavLink,
  MDBBtn,
} from "mdbreact";
import RelatedMovies from "./RelatedMovies";
import "../../App.css";
import { useAuth0 } from "@auth0/auth0-react";
import { getCurrentProfile } from "../../actions/profile";
import { useParams, useHistory, useRouteMatch, Link, Redirect } from "react-router-dom";

const MovieList = ({
  addToCart,
  loadCart,
  isLoading,
  movies,
  fetchApi,
  // page,
  nextPage,
  prevPage,
  dispatch,
  getCurrentProfile,
  userId,
}) => {
  const { getAccessTokenSilently, isAuthenticated, user } = useAuth0();

  const history = useHistory();
  const match = useRouteMatch();
  // const loadMoreMovies = false;
  const loadMoreMovies = movies;


  const [isLoadMore, loadMore] = useState(false);

  const [page, setPage] = useState(1)

  useEffect(() => {
    fetchApi(config.API_KEY, page);
    loadCart();
    // getAccessTokenSilently().then(token => localStorage.setItem("token", token))
    // getCurrentProfile(userId);
    // loadMore(true);
    history.push(`page=${page}`)
    // history.location.state = match.url

  }, [page]);


  let load = (
    <div>
      <SpinnerPage />
    </div>
  );


  // if (!isLoading) {
  //   return <Redirect to={history.location.state}/>
  // }
  // //   window.onscroll = (ev) => {
  //     if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight) {
  //         console.log("you're at the bottom of the page");
  //     }
  // };

  // page transition
  let pages = (
    <nav aria-label="Page navigation example">
      <ul className="pagination">
        <Link
          to={`/movies/${page}`}
          onClick={() => setPage(page <= 1 ? page : page - 1)}
          className="page-item"
        >
          <MDBIcon
            className="white-text pr-3"
            size="2x"
            icon="angle-double-left"
          />
        </Link>
        &nbsp; &nbsp; &nbsp; Page: {page} &nbsp; &nbsp; &nbsp;
        &nbsp;
        <Link
          to={`/movies/${page}`}
          onClick={() => setPage(page + 1)}
          className="page-item"
        >
          <MDBIcon
            className="white-text pr-3"
            size="2x"
            icon="angle-double-right"
          />
        </Link>
      </ul>
    </nav>
  );

  let loadButton = (
    <div>
      <MDBBtn onClick={() => nextPage(page)}>Load More</MDBBtn>
    </div>
  );

  const movieList = (
    <MDBRow>
      {movies.map((movie, key) => {
        return (
          <MDBCol size="6" lg="3">
            <MDBAnimation type="zoomIn" duration="1s">
              <Movie
                id={movie.id}
                addToCart={addToCart}
                title={movie.title}
                image={movie.poster_path}
                overview={movie.overview}
                releaseDate={movie.release_date}
                price={2.99}
                movieObj={movie}
              />
            </MDBAnimation>
          </MDBCol>
        );
      })}
    </MDBRow>
  );

  return (
    <div className="movie-list">
      <MDBContainer>
        <SearchPage />
        <div className="pagination">{pages}</div>
        {isLoading ? load : movieList}
        <div className="pagination">{pages}</div>
        {loadButton}
      </MDBContainer>
      <RelatedMovies />
    </div>
  );
};

MovieList.propTypes = {
  addToCart: PropTypes.func.isRequired,
  loadCart: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  userId: state.profile.profile,
  isLoading: state.movie.isLoading,
  authenticated: state.auth.authenticated,
  movies: state.movie.movies,
  page: state.movie.page,
  searchedMovie: state.movie.searchedMovie,
});

export default connect(mapStateToProps, {
  addToCart,
  loadCart,
  getCart,
  setMovies,
  fetchApi,
  nextPage,
  prevPage,
  getCurrentProfile,
})(MovieList);
