import React, { useEffect, useState } from "react";
import axios from "axios";
import { connect } from "react-redux";
import {
  MDBNav,
  MDBNavItem,
  MDBNavLink,
  MDBView,
  MDBMask,
  MDBContainer,
  MDBCarousel,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBAnimation,
} from "mdbreact";
import { Fade } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";
import "./Landing.css";
import "../MainImage/MainImage.css";
import { Carousel } from "react-responsive-carousel";
import { fetchItems } from "../../actions/movie";
import { slides } from "../../utils/movie_slides";

const Landing = ({}) => {
  let [index, setIndex] = useState(0);

  useEffect(() => {
    timer();
  }, []);

  // useEffect(() => {
  //     setPageNum(pageNum++);
  //     fetchItems(endpoint);
  //     console.log(movies)
  //   }, [index === 10]);

  const timer = () => {
    setInterval(() => {
      setIndex(++index);
      if (index === 19) {
        index = 0;
      } else {
        return;
      }
    }, 5000);
  };

  return (
    <div>
      <MDBCarousel
        activeItem={1}
        length={20}
        showControls={false}
        showIndicators={false}
        className="z-depth-1"
        slide
      >
        <MDBCarouselInner>
          <MDBCarouselItem>
            <MDBView>
              <MDBAnimation type="bounce">
                <img
                  className="d-block w-100"
                  src={`http://image.tmdb.org/t/p/w1280${slides[index].backdrop_path}`}
                  alt="first slide"
                />
              </MDBAnimation>
            </MDBView>
          </MDBCarouselItem>
        </MDBCarouselInner>
      </MDBCarousel>
    </div>
  );
};

export default Landing;
