import React, { useEffect, useState } from "react";
import axios from "axios";
import { connect } from "react-redux";
import {
  MDBNav,
  MDBNavItem,
  MDBNavLink,
  MDBView,
  MDBMask,
  MDBContainer,
  MDBCarousel,
  MDBCarouselInner,
  MDBCarouselItem,
} from "mdbreact";
import { Redirect } from "react-router-dom";

const Landing = ({ movies }) => {
  useEffect(() => {
    movies.map((m, i) => {
      console.log(i)
    })
  },[])

  return (
    <div className="rmdb-moviethumb">
      {movies.map((m, i) => (
        <MDBCarousel
          activeItem={1}
          length={100}
          showControls={false}
          showIndicators={false}
          className="z-depth-1"
          slide
        >
          <MDBCarouselInner>
            <MDBCarouselItem itemId={`${m[i]}`}>
              <MDBView>
                <img
                  className="d-block w-100"
                  src={`http://image.tmdb.org/t/p/w1280${m.poster_path}`}
                  alt="First slide"
                />
              </MDBView>
            </MDBCarouselItem>
          </MDBCarouselInner>
        </MDBCarousel>
      ))}

      {/* <main>
        <MDBContainer className="text-center my-5"></MDBContainer>
      </main> */}
    </div>
  );
};

const mapStateToProps = (state) => ({
  movies: state.movie.movies,
});

export default connect(mapStateToProps, {})(Landing);
