import React, { useEffect, useState } from "react";
import axios from "axios";
import { connect } from "react-redux";
import {
  MDBNav,
  MDBNavItem,
  MDBNavLink,
  MDBView,
  MDBMask,
  MDBContainer,
  MDBCarousel,
  MDBCarouselInner,
  MDBCarouselItem,
} from "mdbreact";
import { Fade } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";
import "./Landing.css";
import "../MainImage/MainImage.css";
import { Carousel } from 'react-responsive-carousel';


const Landing = ({ movies }) => {
  // let i = 0;

  // useEffect(() => {
  //   timer()
  // }, []);

  // const timer = () => {
  //   setInterval(() => {

  //     console.log(i);
  //   }, 3000);
  // };

  return (
    <div>
      {movies.map((m, i) => (
        <Carousel>
          <MDBCarousel
            activeItem={1}
            length={20}
            showControls={false}
            showIndicators={false}
            className="z-depth-1"
            slide
          >
            <MDBCarouselInner>
              <MDBCarouselItem>
                <MDBView>
                  <img
                    className="d-block w-100"
                    src={`http://image.tmdb.org/t/p/w1280${m.backdrop_path}`}
                    alt="first slide"
                  />
                </MDBView>
              </MDBCarouselItem>
            </MDBCarouselInner>
          </MDBCarousel>
        </Carousel>
      ))}
    </div>
  );
};

const mapStateToProps = (state) => ({
  movies: state.movie.movies,
});

export default connect(mapStateToProps, {})(Landing);
