import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import SpinnerPage from "../Layout/SpinnerPage";
import { addToCart, loadCart } from "../../actions/cart";
import {
  setMovies,
  nextPage,
  prevPage,
  loadMore,
  loadMovies,
  loadMoreItems
} from "../../actions/movie";
import { connect } from "react-redux";
import Movie from "./Movie";
import SearchPage from "../Search/Search";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBBtn,
} from "mdbreact";
import "../../App.css";
import { useAuth0 } from "@auth0/auth0-react";
import { useHistory } from "react-router-dom";

const MovieList = ({
  addToCart,
  loadCart,
  isLoading,
  movies,
  loadMore,
  loadMoreItems
}) => {
  const { getAccessTokenSilently, isAuthenticated, user } = useAuth0();

  const history = useHistory();

  // setMovies();
  useEffect(() => {
    loadCart();
  }, []);

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  let load = (
    <div>
      <SpinnerPage />
    </div>
  );

  let loadMoreBtn = (
    <div>
      <MDBBtn onClick={() => loadMore()}>Load More</MDBBtn>
    </div>
  );

  const movieList = (
    <MDBRow>
      {movies.map((movie, index) => {
        return (
          <MDBCol middle="true" size="3">
            <Movie
              index={index}
              id={movie.id}
              addToCart={addToCart}
              title={movie.title}
              image={movie.poster_path}
              overview={movie.overview}
              releaseDate={movie.release_date}
              price={2.99}
              movieObj={movie}
            />
          </MDBCol>
        );
      })}
    </MDBRow>
  );

  return (
    <div>
      <MDBContainer>
        <SearchPage />
        <MDBContainer>{isLoading ? load : movieList} </MDBContainer>
        <LoadMoreBtn text="Load More" onClick={() => loadMoreItems()} />
      </MDBContainer>
    </div>
  );
};

MovieList.propTypes = {
  addToCart: PropTypes.func.isRequired,
  loadCart: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  userId: state.auth.userInfo._id,
  isLoading: state.movie.isLoading,
  authenticated: state.auth.authenticated,
  movies: state.movie.movies,
  page: state.movie.moviePage,
  searchedMovie: state.movie.searchedMovie,
});

export default connect(mapStateToProps, {
  addToCart,
  loadCart,
  setMovies,
  loadMoreItems,
  nextPage,
  prevPage,
  loadMore,
  loadMovies,
})(MovieList);
